import React from "react";
import {Link} from 'react-router-dom';
import { Navbar, Nav } from "react-bootstrap";
handleLogin = (event) => {
    event.preventDefault();
    let email = document.getElementById('email').value;
    let password = document.getElementById('password').value;
    let data = {
        email: email,
        password: password
    }
    let settings = {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json'
        },
        body : JSON.stringify( data )
    }
    fetchAPI("/users/login", settings)
    .then( response => {
        return response.json();
    })
    .then( login => {
        localStorage.setItem("accessToken",login.token);
        this.props.history.push("/feed");
    })
    .catch( err => {
        console.log(err);
    })
}
const NavBar = ({searchVisible}) => { const handleLogin = (event) => {
    event.preventDefault();
    let email = document.getElementById('email').value;
    let password = document.getElementById('password').value;
    let data = {
        email: email,
        password: password
    }
    let settings = {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json'
        },
        body : JSON.stringify( data )
    }
    fetchAPI("/users/login", settings)
    .then( response => {
        return response.json();
    })
    .then( login => {
        localStorage.setItem("accessToken",login.token);
        this.props.history.push("/feed");
    })
    .catch( err => {
        console.log(err);
    })
}
return (<Navbar expand="lg" style={{backgroundColor: '#03989e'}}>
<div className="container">
    <Navbar.Brand href="/feed">Kencho</Navbar.Brand>
    <div hidden={searchVisible}>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="mr-auto">
                <Nav.Link as={Link} to="/liked-posts">Mis Likes</Nav.Link>
                <Nav.Link as={Link} to="/activity-log">Mi Actividad</Nav.Link>
                <Nav.Link as={Link}to="/add-post">Añadir Meme</Nav.Link>
                <Nav.Link as={Link} to="/">Cerrar Sesión</Nav.Link>
            </Nav>
        </Navbar.Collapse>
    </div>
</div>
</Navbar>)};
export default NavBar;
